public class ControllerCertification
{

	// SINGLETON
	public static ControllerCertification getInstance()
	{
		if (null == instance)
		{ // Premier appel
			instance = new ControllerCertification();
		}
		return instance;
	}

	/**
	 * Constructeur redéfini comme étant privé pour interdire son appel et
	 * forcer à passer par la méthode <link
	 */
	private ControllerCertification()
	{
	}

	/** L'instance statique */
	private static ControllerCertification instance;

	public boolean certificationPDF(String identifiant, ArrayList<String> urls)
	{
		if (null == identifiant || 0 == identifiant.length())
			return false;
		if (null == urls || 0 == urls.size())
			return false;
		boolean isOk = true;
		for (String url : urls)
		{
			if (!DAOUtilisateur.getInstance().certifiedDocument(identifiant,
					url))
				isOk = false;
		}
		return isOk;
	}

	// TODO : Mettre les attributs dans un fichier de config pour parametrage
	public HashMap<String, String> certificationPDF(String identifiant,
			String url, String urlRetour, String saveFile, String certFolder)
	{
		HashMap<String, String> toReturn = new HashMap<String, String>();

		// ------------------Depend de la Base
		// ----------------------------------------
		System.out.println("[TEST KEYNECTIS] Debut de la certification");

		Utilisateur user = DAOUtilisateur.getInstance().getUserByIdentifiant(
				identifiant);
		DocumentPDF document = getDocumentOfUserByUrl(user, url);

		// --------------------------Variable parametrable en fonction du metier
		// / fichier de config?--------
		String pathCertificat = certFolder + "/demoqs_s.p12";
		System.out.println("[TEST KEYNECTIS] Adresse certificat : "
				+ pathCertificat);
		String motPasse = "DemoQS";
		// String adresseXML="ressources/Test.pdf.xml";
		String adresseXML = "";
		// Ajout d'une zone de signature au pdf

		String urlPdfToEncode = "";

		System.out.println("[TEST KEYNECTIS] Debut PDF avec signature");

		try
		{
			urlPdfToEncode = ToolsPDF.createPDFDocToSign(document.getUrl(),
					saveFile, document.getName());
		}
		catch (DocumentException e1)
		{
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}
		catch (IOException e1)
		{
			// TODO Auto-generated catch block
			e1.printStackTrace();
		}
		System.out
				.println("[TEST KEYNECTIS] Fichier PDF avec signature Créé ! ");
		try
		{
			adresseXML = ToolsPDF.pdf2xml(urlPdfToEncode, document.getName(),
					saveFile);
		}
		catch (IOException e)
		{
			// TODO Auto-generated catch block
			e.printStackTrace();
			return null;
		}
		if (adresseXML.equals(""))
			return null;
		System.out.println("[TEST KEYNECTIS] Fichier XML du PDF Créé ! ");

		// -------------------------Creation du certificat de signature
		// metier-----------------------
		System.out.println("[TEST KEYNECTIS] Debut de la signature");
		String origMetierSign = "";
		try
		{
			origMetierSign = com.dictao.keynectis.quicksign.transid.Util
					.signXmlFileDsig(adresseXML, pathCertificat, motPasse);
		}
		catch (QuickSignException e)
		{
			e.printStackTrace();
			return null;
		}
		if (origMetierSign.equals(""))
			return null;
		System.out.println("[TEST KEYNECTIS] Fin de la signature");

		// ----------------------Calcul du Hash
		// -----------------------------------------------------
		System.out.println("[TEST KEYNECTIS] Debut du calcul du HASH");
		String hashBase64 = "";
		try
		{
			hashBase64 = com.dictao.keynectis.quicksign.transid.Util
					.getB64Hash(origMetierSign);
		}
		catch (QuickSignException e)
		{
			// TODO Auto-generated catch block
			e.printStackTrace();
			return null;
		}
		if (hashBase64.equals(""))
			return null;
		System.out.println("[TEST KEYNECTIS] Fin du calcul du HASH");

		// ---------------------Informations
		// Utilisateur-----------------------------------------------
		String nom = user.getLastName();
		String prenom = user.getFirstName();
		String email = user.getEmail();
		String authority = "KWS_INTEGRATION_CDS";
		// System.out.println("[TEST KEYNECTIS] Debut de la génération du CUF");
		// String cuf = String.valueOf(1000 + (new java.util.Random())
		// .nextInt(8999));
		// System.out.println("[TEST KEYNECTIS] Fin de la génération du CUF");

		// String basePath = request.getScheme() + "://"
		// + request.getServerName() + ":" + request.getServerPort()
		// + request.getContextPath() + request.getServletPath();

		// String returnUrl = urlRetour.substring(0,
		// urlRetour.lastIndexOf("/"))+ "/demoPDFSMS/demo6p4.jsp";
		String returnUrl = urlRetour.substring(0, urlRetour.lastIndexOf("/"))
				+ "/ResponseKeynectis";
		// String returnUrl = urlRetour;
		System.out.println("[TEST KEYNECTIS] url de retour : " + returnUrl);

		String blob = "";
		String transNum = "";
		// BufferedReader br = null;
		// String ligne = "";
		String tag = "";

		System.out
				.println("[TEST KEYNECTIS] Debut de l'encodage de la signature");

		// TODO possible problème d'encodage
		// byte[] decode = EncoderBase64.encodingBlobToByteArray(user
		// .getSignature());
		String signatureBase64 = "\n";
		// EncoderBase64.byteArraytoStringBase64(decode);

		System.out.println("[TEST KEYNECTIS] Fin encodage de la signature");

		// -----------------------------DEFINITION DU
		// TAG---------------------------------------------------------
		System.out.println("[TEST KEYNECTIS] Debut de la définition du TAG");

		tag += "DATA_METIER=contrat\n";
		tag += "CUF_ORG=no\n";
		tag += "TYPE=38\n";
		tag += "VISU=docPDFb64\n";
		// tag += "PDF_VERIFY_FIELDS=Signature1\n";
		// tag += "PDF_SIGN_FIELD=Signature2\n";
		tag += "PDF_SIGN_FIELD=Signature1\n";
		// tag += "PDF_REASON=Test avec image\n"; //Motif de la signature:
		// Conseillé mais facultatif
		// tag += "PDF_LOCATION=Capgemini France | Paris, Rue de Lyon\n"; //Lieu
		// de la signature: Conseillé mais facultatif
		// tag += "PDF_CONTACT=01.49.67.44.11\n"; //Coordonnées du signataire:
		// Conseillé mais facultatif
		// tag += "PDF_SIGNATURE_GRAPHIC="+signatureBase64+"\n";

		tag += "PDF_SIGNATURE_LAYER2_TEXT=\n";
		// tag +=
		// "PDF_SIGNATURE_BACKGROUND_\n";
		tag += "PDF_SIGNATURE_BACKGROUND_IMAGE=" + signatureBase64;// + "\n";
		tag += "PDF_SIGNATURE_BACKGROUND_IMAGE_SCALE=-1\n";
		// tag += "EXTRA_PARAM=demo=demoPDFSMS;numTelSMS="
		// + user.getPhoneNumber()
		// + ";smsBody=Bonjour. Voici votre code de signature : "
		// + cuf + ". "
		// + " Keynectis vous remercie de votre confiance.\n";

		System.out.println("[TEST KEYNECTIS] Fin de la définition du TAG");

		// --------------------------DEFINTION DE
		// TRANSID--------------------------------------
		System.out
				.println("[TEST KEYNECTIS] Debut de la définition de TRANSID");

		String identifiant_application_metier = "ZZDEMAV1";
		String identifiant_application_serveur_metier = "DEMO";
		String identifiant_organisme_application_metier = "PDFSMS";

		String path_certificat_signature_blob = certFolder + "/demoqs_i.p12";
		String mot_de_passe_certificat_blob = "DemoQS";

		String path_certificat_chiffrement_blob = certFolder
				+ "/certQSkeyncryp.cer";
		RequestTransId rti = null;
		try
		{
			rti = new RequestTransId(identifiant_application_metier,
					identifiant_application_serveur_metier,
					identifiant_organisme_application_metier);
		}
		catch (QuickSignException e)
		{
			// TODO Auto-generated catch block
			e.printStackTrace();
			return null;
		}

		// if (rti == null)
		// return null;

		rti.setSignCertFilePath(path_certificat_signature_blob,
				mot_de_passe_certificat_blob);
		rti.setCipherCertFilePath(path_certificat_chiffrement_blob);
		rti.setName(prenom + " " + nom);
		rti.setEmail(email);
		rti.setAuthority(authority);
		// rti.setCuf(cuf);
		rti.setReturnUrl(returnUrl);
		rti.setFilePath(origMetierSign); // L'original métier signé en pj
		rti.setTag(tag);

		try
		{
			transNum = rti.getTransNum();
			// session.setAttribute("transNum", transNum);
			blob = rti.getB64Blob();
		}
		catch (QuickSignException qse)
		{
			return null;
		}
		System.out.println("[TEST KEYNECTIS] Fin de la définition de TRANSID");

		toReturn.put("blob", blob);
		toReturn.put("transNum", transNum);

		return toReturn;
	}

	/**
	 * Find the document of a user by its url
	 * 
	 * @param user
	 * @param url
	 * @return the document if it has been found or null if not
	 */
	private DocumentPDF getDocumentOfUserByUrl(Utilisateur user, String url)
	{
		for (DocumentPDF doc : user.getDocuments())
		{
			if (doc.getUrl().equals(url))
				return doc;
		}
		return null;
	}
}
